import React, { useContext } from 'react';
import {
  Card,
  CardMedia,
  CardContent,
  Typography,
  CardActions,
  Button,
  Box
} from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';
import StarIcon from '@mui/icons-material/Star';
import { AuthContext } from '../contexts/AuthContext';
import API from '../api/api';

export default function MovieCard({ movie, view = 'grid' }) {
  const { admin } = useContext(AuthContext);
  const navigate = useNavigate();

  const isGrid = view === 'grid';

  async function handleDelete() {
    if (!window.confirm('Delete this movie?')) return;
    try {
      await API.delete(`/movies/${movie._id}`);
      window.location.reload();
    } catch {
      alert('Delete failed');
    }
  }

  return (
    <Card
      sx={{
        height: '100%',
        display: 'flex',
        flexDirection: isGrid ? 'column' : { xs: 'column', sm: 'row' }
      }}
    >
      {/* Poster container */}
      <Box
        sx={{
          width: isGrid ? '100%' : 180,
          height: isGrid ? 280 : 260,
          bgcolor: '#f4f4f4',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center'
        }}
      >
        <CardMedia
          component="img"
          image={movie.poster || 'https://via.placeholder.com/300x450?text=No+Image'}
          alt={movie.title}
          sx={{
            maxHeight: '100%',
            maxWidth: '100%',
            objectFit: 'contain'
          }}
        />
      </Box>

      {/* Content */}
      <CardContent
        sx={{
          flexGrow: 1,
          display: 'flex',
          flexDirection: 'column'
        }}
      >
        <Typography variant="h6" noWrap>
          {movie.title}
        </Typography>

        <Typography
          variant="body2"
          sx={{
            mt: 1,
            flexGrow: 1,
            display: '-webkit-box',
            WebkitLineClamp: isGrid ? 3 : 4,
            WebkitBoxOrient: 'vertical',
            overflow: 'hidden'
          }}
        >
          {movie.description || 'No description available.'}
        </Typography>

        <Box sx={{ mt: 1, display: 'flex', alignItems: 'center', gap: 0.5 }}>
          <StarIcon fontSize="small" />
          <Typography variant="body2">
            {movie.rating ?? '—'}
          </Typography>
        </Box>
      </CardContent>

      {/* Actions */}
      <CardActions
        sx={{
          mt: 'auto',
          px: 2,
          pb: 2,
          justifyContent: admin ? 'space-between' : 'flex-end'
        }}
      >
        <Button
          size="small"
          component={Link}
          to={`/movie/${movie._id}`}
        >
          View
        </Button>

        {admin && (
          <Box sx={{ display: 'flex', gap: 1 }}>
            <Button
              size="small"
              onClick={() => navigate(`/admin/edit/${movie._id}`)}
            >
              Edit
            </Button>
            <Button
              size="small"
              color="error"
              onClick={handleDelete}
            >
              Delete
            </Button>
          </Box>
        )}
      </CardActions>
    </Card>
  );
}
