import React, { useState } from 'react';
import API from '../api/api';
import {
  Box,
  TextField,
  Button,
  Grid,
  Typography,
  CircularProgress
} from '@mui/material';
import MovieCard from '../components/MovieCard';

export default function SearchPage() {
  const [q, setQ] = useState('');
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  async function handleSearch(e) {
    e.preventDefault();
    if (!q.trim()) return;

    try {
      setLoading(true);
      setError('');

      const res = await API.get('/movies/search', {
        params: { q }
      });

      /*
        Backend may return:
        1) [ ... ]
        2) { movies: [...] }
      */
      const payload = res.data;
      const resultArray = Array.isArray(payload)
        ? payload
        : Array.isArray(payload.movies)
          ? payload.movies
          : [];

      setMovies(resultArray);
    } catch (err) {
      console.error('Search error:', err);
      setError('Failed to search movies');
      setMovies([]);
    } finally {
      setLoading(false);
    }
  }

  return (
    <Box>
      <Typography variant="h5" fontWeight={600} mb={2}>
        Search Movies
      </Typography>

      {/* Search Form */}
      <Box
        component="form"
        onSubmit={handleSearch}
        sx={{
          display: 'flex',
          flexDirection: { xs: 'column', sm: 'row' },
          gap: 1,
          mb: 3
        }}
      >
        <TextField
          value={q}
          onChange={(e) => setQ(e.target.value)}
          label="Search by title or description"
          fullWidth
          size="small"
        />
        <Button
          type="submit"
          variant="contained"
          disabled={loading}
        >
          Search
        </Button>
      </Box>

      {/* Loading */}
      {loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <CircularProgress />
        </Box>
      )}

      {/* Error */}
      {error && (
        <Typography color="error" align="center" sx={{ mt: 3 }}>
          {error}
        </Typography>
      )}

      {/* No results */}
      {!loading && !error && movies.length === 0 && q.trim() && (
        <Typography align="center" sx={{ mt: 4 }}>
          No movies found.
        </Typography>
      )}

      {/* Results */}
      <Grid container spacing={2}>
        {movies.map((movie) => (
          <Grid
            item
            key={movie._id}
            xs={12}
            sm={6}
            md={4}
            lg={3}
          >
            <MovieCard movie={movie} />
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
