import React, { useEffect, useState } from 'react';
import API from '../api/api';
import { Grid, Box, Typography, Select, MenuItem, FormControl, Pagination, ToggleButton, ToggleButtonGroup } from '@mui/material';
import MovieCard from '../components/MovieCard';

export default function Home() {
  const [movies, setMovies] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [limit] = useState(12);
  const [sortBy, setSortBy] = useState('title');
  const [view, setView] = useState('grid');

  useEffect(() => fetchPage(1), [sortBy]);

  async function fetchPage(p = 1) {
    const res = await API.get('/movies', { params: { page: p, limit }});
    setMovies(res.data.movies);
    setPage(res.data.page);
    setTotalPages(res.data.totalPages);
  }

  function handlePage(_, v) { fetchPage(v); }

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="h5">All Movies</Typography>
        <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
          <FormControl size="small">
            <Select value={sortBy} onChange={(e)=> setSortBy(e.target.value)}>
              <MenuItem value="title">Name</MenuItem>
              <MenuItem value="rating">Rating</MenuItem>
              <MenuItem value="releaseDate">Release Date</MenuItem>
              <MenuItem value="duration">Duration</MenuItem>
            </Select>
          </FormControl>
          <ToggleButtonGroup value={view} exclusive onChange={(_,v)=> v && setView(v)}>
            <ToggleButton value="grid">Grid</ToggleButton>
            <ToggleButton value="list">List</ToggleButton>
          </ToggleButtonGroup>
        </Box>
      </Box>

      <Grid container spacing={2}>
        {movies.map(m => (
          <Grid item key={m._id} xs={12} sm={6} md={4} lg={3}>
            <MovieCard movie={m} />
          </Grid>
        ))}
      </Grid>

      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 3 }}>
        <Pagination count={Math.max(1,totalPages)} page={page} onChange={handlePage} />
      </Box>
    </Box>
  );
}
