import React, { useEffect, useState } from 'react';
import API from '../api/api';
import {
  Grid,
  Box,
  Typography,
  Select,
  MenuItem,
  FormControl,
  Pagination,
  ToggleButton,
  ToggleButtonGroup,
  CircularProgress,
  Paper
} from '@mui/material';
import { motion } from 'framer-motion';
import MovieCard from '../components/MovieCard';

export default function Home() {
  const [movies, setMovies] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [limit] = useState(12);
  const [sortBy, setSortBy] = useState('title');
  const [order] = useState('asc');
  const [view, setView] = useState('grid');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchMovies(1);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [sortBy]);

  async function fetchMovies(p = 1) {
    try {
      setLoading(true);
      setError('');

      const res = await API.get('/movies/sorted', {
        params: {
          by: sortBy,
          order,
          page: p,
          limit
        }
      });

      setMovies(res.data.movies || []);
      setPage(res.data.page || p);
      setTotalPages(res.data.totalPages || 1);
    } catch (err) {
      console.error(err);
      setError('Failed to load movies');
      setMovies([]);
    } finally {
      setLoading(false);
    }
  }

  function handlePage(_, value) {
    fetchMovies(value);
  }

  return (
    <Box>
      {/* HERO HEADER */}
      <Paper
        elevation={0}
        sx={{
          p: 3,
          mb: 4,
          background: 'linear-gradient(135deg, #1c1c1c, #111)',
          borderRadius: 3
        }}
      >
        <Typography variant="h4" fontWeight={700}>
          ðŸŽ¬ Top Movies
        </Typography>
        <Typography variant="body2" color="text.secondary" mt={1}>
          Browse IMDb Top-rated movies, sort, search and manage with ease
        </Typography>

        {/* CONTROLS */}
        <Box
          sx={{
            display: 'flex',
            flexWrap: 'wrap',
            gap: 2,
            mt: 3,
            alignItems: 'center'
          }}
        >
          <FormControl size="small">
            <Select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
            >
              <MenuItem value="title">Name</MenuItem>
              <MenuItem value="rating">Rating</MenuItem>
              <MenuItem value="releaseDate">Release Date</MenuItem>
              <MenuItem value="duration">Duration</MenuItem>
            </Select>
          </FormControl>

          <ToggleButtonGroup
            value={view}
            exclusive
            size="small"
            onChange={(_, v) => v && setView(v)}
          >
            <ToggleButton value="grid">Grid</ToggleButton>
            <ToggleButton value="list">List</ToggleButton>
          </ToggleButtonGroup>
        </Box>
      </Paper>

      {/* LOADING */}
      {loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 6 }}>
          <CircularProgress />
        </Box>
      )}

      {/* ERROR */}
      {error && (
        <Typography color="error" align="center" sx={{ mt: 4 }}>
          {error}
        </Typography>
      )}

      {/* EMPTY */}
      {!loading && !error && movies.length === 0 && (
        <Typography align="center" sx={{ mt: 4 }}>
          No movies found.
        </Typography>
      )}

      {/* MOVIES GRID / LIST */}
      <Grid container spacing={3}>
        {movies.map((movie, index) => (
          <Grid
            item
            key={movie._id}
            xs={12}
            sm={view === 'grid' ? 6 : 12}
            md={view === 'grid' ? 4 : 12}
            lg={view === 'grid' ? 3 : 12}
          >
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: index * 0.03 }}
            >
              <MovieCard movie={movie} view={view} />
            </motion.div>
          </Grid>
        ))}
      </Grid>

      {/* PAGINATION */}
      {totalPages > 1 && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 5 }}>
          <Pagination
            count={totalPages}
            page={page}
            onChange={handlePage}
            color="primary"
            size="large"
          />
        </Box>
      )}
    </Box>
  );
}
