import React, { useEffect, useState } from 'react';
import API from '../api/api';
import {
  Box,
  Typography,
  TextField,
  Button,
  CircularProgress
} from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';

export default function AdminEdit() {
  const { id } = useParams();
  const nav = useNavigate();

  const [form, setForm] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchMovie() {
      try {
        setLoading(true);
        setError('');

        const res = await API.get('/movies', {
          params: { page: 1, limit: 2000 }
        });

        /*
          Backend may return:
          1) { movies: [...] }
          2) [ ... ]
        */
        const payload = res.data;
        const list = Array.isArray(payload)
          ? payload
          : Array.isArray(payload.movies)
            ? payload.movies
            : [];

        const found = list.find(
          m => m._id === id || m.id === id
        );

        setForm(found || null);
      } catch (err) {
        console.error('AdminEdit fetch error:', err);
        setError('Failed to load movie');
        setForm(null);
      } finally {
        setLoading(false);
      }
    }

    fetchMovie();
  }, [id]);

  async function submit(e) {
    e.preventDefault();
    try {
      await API.put(`/movies/${id}`, {
        ...form,
        rating: form.rating !== '' ? Number(form.rating) : null,
        duration: form.duration !== '' ? Number(form.duration) : null
      });
      nav('/');
    } catch (err) {
      alert(err.response?.data?.message || 'Update failed');
    }
  }

  async function handleDelete() {
    if (!window.confirm('Delete movie?')) return;
    try {
      await API.delete(`/movies/${id}`);
      nav('/');
    } catch (err) {
      alert(err.response?.data?.message || 'Delete failed');
    }
  }

  // Loading state
  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 6 }}>
        <CircularProgress />
      </Box>
    );
  }

  // Error or not found
  if (error || !form) {
    return (
      <Typography align="center" color="error" sx={{ mt: 6 }}>
        Movie not found or failed to load
      </Typography>
    );
  }

  return (
    <Box maxWidth={720} mx="auto" sx={{ py: 4 }}>
      <Typography variant="h5" fontWeight={600} mb={2}>
        Edit Movie
      </Typography>

      <Box
        component="form"
        onSubmit={submit}
        sx={{
          display: 'flex',
          flexDirection: 'column',
          gap: 2
        }}
      >
        <TextField
          label="Title"
          value={form.title || ''}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
          required
        />

        <TextField
          label="Description"
          value={form.description || ''}
          onChange={(e) => setForm({ ...form, description: e.target.value })}
          multiline
          rows={4}
        />

        <TextField
          label="Rating"
          type="number"
          inputProps={{ step: '0.1', min: 0, max: 10 }}
          value={form.rating ?? ''}
          onChange={(e) => setForm({ ...form, rating: e.target.value })}
        />

        <TextField
          label="Duration (minutes)"
          type="number"
          value={form.duration ?? ''}
          onChange={(e) => setForm({ ...form, duration: e.target.value })}
        />

        <TextField
          label="Release Date"
          type="date"
          value={
            form.releaseDate
              ? new Date(form.releaseDate).toISOString().split('T')[0]
              : ''
          }
          onChange={(e) =>
            setForm({ ...form, releaseDate: e.target.value })
          }
          InputLabelProps={{ shrink: true }}
        />

        <TextField
          label="Poster URL"
          value={form.poster || ''}
          onChange={(e) => setForm({ ...form, poster: e.target.value })}
        />

        <Box sx={{ display: 'flex', gap: 2, mt: 2 }}>
          <Button type="submit" variant="contained">
            Save
          </Button>
          <Button
            variant="outlined"
            color="error"
            onClick={handleDelete}
          >
            Delete
          </Button>
        </Box>
      </Box>
    </Box>
  );
}
