import React, { useContext } from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Box,
  IconButton
} from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import MenuIcon from '@mui/icons-material/Menu';

export default function Navbar() {
  const { admin, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  return (
    <AppBar position="sticky">
      <Toolbar>
        <IconButton
          edge="start"
          color="inherit"
          aria-label="menu"
          sx={{ mr: 2 }}
        >
          <MenuIcon />
        </IconButton>

        <Typography
          component={Link}
          to="/"
          variant="h6"
          sx={{
            color: 'inherit',
            textDecoration: 'none',
            flexGrow: 1,
            fontWeight: 600
          }}
        >
          MovieApp
        </Typography>

        <Box sx={{ display: 'flex', gap: 1 }}>
          {/* Public */}
          <Button color="inherit" component={Link} to="/search">
            Search
          </Button>

          {/* Admin-only */}
          {admin ? (
            <>
              <Button color="inherit" component={Link} to="/admin/add">
                Admin
              </Button>
              <Button
                color="inherit"
                onClick={() => {
                  logout();
                  navigate('/');
                }}
              >
                Logout
              </Button>
            </>
          ) : (
            <Button
              color="inherit"
              component={Link}
              to="/admin/login"
            >
              Admin Login
            </Button>
          )}
        </Box>
      </Toolbar>
    </AppBar>
  );
}
