import React, { useEffect, useState, useCallback } from 'react';
import API from '../api/api';
import {
  Grid,
  Box,
  Typography,
  Select,
  MenuItem,
  FormControl,
  Pagination,
  ToggleButton,
  ToggleButtonGroup,
  CircularProgress
} from '@mui/material';
import { motion } from 'framer-motion';
import MovieCard from '../components/MovieCard';

export default function Home() {
  const [movies, setMovies] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [limit] = useState(12);
  const [sortBy, setSortBy] = useState('title');
  const [order] = useState('asc');
  const [view, setView] = useState('grid');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // fetchMovies is stable via useCallback so effect deps are correct
  const fetchMovies = useCallback(
    async function fetchMoviesInner(p = 1) {
      try {
        setLoading(true);
        setError('');

        const res = await API.get('/movies/sorted', {
          params: {
            by: sortBy,
            order,
            page: p,
            limit
          }
        });

        setMovies(res.data.movies || []);
        setPage(res.data.page || p);
        setTotalPages(res.data.totalPages || 1);
      } catch (err) {
        console.error(err);
        setError('Failed to load movies');
        setMovies([]);
        setPage(1);
        setTotalPages(1);
      } finally {
        setLoading(false);
      }
    },
    [sortBy, order, limit]
  );

  useEffect(() => {
    fetchMovies(1);
  }, [fetchMovies]);

  function handlePage(_, value) {
    fetchMovies(value);
  }

  return (
    <Box>
      {/* TOP CONTROLS */}
      <Box
        sx={{
          display: 'flex',
          flexWrap: 'wrap',
          gap: 2,
          mb: 3,
          alignItems: 'center',
          justifyContent: 'space-between'
        }}
      >
        <Typography variant="h6" fontWeight={600}>
          Movies
        </Typography>

        <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
          <FormControl size="small" sx={{ minWidth: 160 }}>
            <Select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
            >
              <MenuItem value="title">Name</MenuItem>
              <MenuItem value="rating">Rating</MenuItem>
              <MenuItem value="releaseDate">Release Date</MenuItem>
              <MenuItem value="duration">Duration</MenuItem>
            </Select>
          </FormControl>

          <ToggleButtonGroup
            value={view}
            exclusive
            size="small"
            onChange={(_, v) => v && setView(v)}
          >
            <ToggleButton value="grid">Grid</ToggleButton>
            <ToggleButton value="list">List</ToggleButton>
          </ToggleButtonGroup>
        </Box>
      </Box>

      {/* LOADING */}
      {loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 6 }}>
          <CircularProgress />
        </Box>
      )}

      {/* ERROR */}
      {error && (
        <Typography color="error" align="center" sx={{ mt: 4 }}>
          {error}
        </Typography>
      )}

      {/* MOVIES */}
      {!loading && !error && movies.length > 0 && (
        <Grid container spacing={3}>
          {movies.map((movie, index) => (
            <Grid
              item
              key={movie._id}
              xs={12}
              sm={view === 'grid' ? 6 : 12}
              md={view === 'grid' ? 4 : 12}
              lg={view === 'grid' ? 3 : 12}
            >
              <motion.div
                initial={{ opacity: 0, y: 15 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.25, delay: index * 0.03 }}
              >
                <MovieCard movie={movie} view={view} />
              </motion.div>
            </Grid>
          ))}
        </Grid>
      )}

      {/* EMPTY STATE */}
      {!loading && !error && movies.length === 0 && (
        <Typography align="center" color="text.secondary" sx={{ mt: 6 }}>
          No movies available.
        </Typography>
      )}

      {/* PAGINATION */}
      {totalPages > 1 && !loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 5 }}>
          <Pagination
            count={totalPages}
            page={page}
            onChange={handlePage}
            color="primary"
            size="large"
          />
        </Box>
      )}
    </Box>
  );
}
