import React from 'react';
import { Card, CardMedia, CardContent, Typography, CardActions, Button } from '@mui/material';
import { Link } from 'react-router-dom';
import StarIcon from '@mui/icons-material/Star';

export default function MovieCard({ movie }) {
  return (
    <Card sx={{ display: 'flex', flexDirection: { xs: 'column', sm: 'row' }, height: '100%' }}>
      <CardMedia
        component="img"
        src={movie.poster || 'https://via.placeholder.com/300x450?text=No+Image'}
        alt={movie.title}
        sx={{ width: { xs: '100%', sm: 160 }, objectFit: 'cover' }}
      />
      <CardContent sx={{ flex: 1 }}>
        <Typography variant="h6" noWrap>{movie.title}</Typography>
        <Typography variant="body2" sx={{ mt: 1, display: '-webkit-box', WebkitLineClamp: 3, WebkitBoxOrient: 'vertical', overflow: 'hidden' }}>
          {movie.description || 'No description available.'}
        </Typography>
        <Typography variant="caption" display="block" sx={{ mt: 1 }}>
          <StarIcon fontSize="small" sx={{ verticalAlign: 'middle' }} /> {movie.rating ?? '—'}
        </Typography>
      </CardContent>
      <CardActions sx={{ alignItems: 'flex-end', pr: 2 }}>
        <Button component={Link} to={`/movie/${movie._id}`} size="small">View</Button>
      </CardActions>
    </Card>
  );
}
