import React, { useEffect, useState } from 'react';
import API from '../api/api';
import { useParams } from 'react-router-dom';
import { Box, Typography, Paper } from '@mui/material';

export default function MovieDetails(){
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  useEffect(() => {
    API.get('/movies', { params: { page: 1, limit: 500 }})
      .then(res => {
        const f = res.data.movies.find(m => m._id === id);
        setMovie(f || null);
      }).catch(console.error);
  }, [id]);

  if (!movie) return <Box>Loading...</Box>;

  return (
    <Paper sx={{ p: 2 }}>
      <Box sx={{ display: 'flex', gap: 2, flexDirection: { xs: 'column', sm: 'row' } }}>
        <img src={movie.poster || 'https://via.placeholder.com/300x450'} alt={movie.title} style={{ width: 260, objectFit: 'cover' }}/>
        <Box>
          <Typography variant="h4">{movie.title}</Typography>
          <Typography variant="subtitle1" sx={{ mt: 1 }}>{movie.releaseDate ? new Date(movie.releaseDate).getFullYear() : ''} • {movie.duration ? `${movie.duration} min` : ''}</Typography>
          <Typography sx={{ mt: 2 }}>{movie.description}</Typography>
          <Typography sx={{ mt: 2 }}>Rating: {movie.rating ?? '—'}</Typography>
        </Box>
      </Box>
    </Paper>
  );
}
