import React, { useState } from 'react';
import API from '../api/api';
import {
  Box,
  TextField,
  Button,
  Grid,
  Typography,
  CircularProgress,
  Autocomplete,
  Avatar
} from '@mui/material';
import StarIcon from '@mui/icons-material/Star';
import { useNavigate } from 'react-router-dom';
import MovieCard from '../components/MovieCard';

export default function SearchPage() {
  const [q, setQ] = useState('');
  const [movies, setMovies] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [searched, setSearched] = useState(false);

  const navigate = useNavigate();

  // üîé Button-based search
  async function handleSearch(e) {
    e.preventDefault();
    if (!q.trim()) return;

    try {
      setLoading(true);
      setError('');
      setSearched(true);

      const res = await API.get('/movies/search', { params: { q } });
      const payload = res.data;

      const resultArray = Array.isArray(payload)
        ? payload
        : Array.isArray(payload.movies)
          ? payload.movies
          : [];

      setMovies(resultArray);
    } catch (err) {
      console.error('Search error:', err);
      setError('Failed to search movies');
      setMovies([]);
    } finally {
      setLoading(false);
    }
  }

  // üí° Live suggestions
  async function handleSuggestion(_, value) {
    setQ(value);

    if (!value || value.length < 2) {
      setSuggestions([]);
      return;
    }

    try {
      const res = await API.get('/movies/search', { params: { q: value } });
      const payload = res.data;

      const arr = Array.isArray(payload)
        ? payload
        : Array.isArray(payload.movies)
          ? payload.movies
          : [];

      setSuggestions(arr.slice(0, 8)); // limit suggestions
    } catch {
      setSuggestions([]);
    }
  }

  return (
    <Box>
      <Typography variant="h5" fontWeight={600} mb={2}>
        Search Movies
      </Typography>

      {/* üîç BIG SEARCH BAR WITH RICH SUGGESTIONS */}
      <Box
        component="form"
        onSubmit={handleSearch}
        sx={{
          display: 'flex',
          flexDirection: { xs: 'column', sm: 'row' },
          gap: 2,
          mb: 4
        }}
      >
        <Autocomplete
          freeSolo
          options={suggestions}
          onInputChange={handleSuggestion}
          getOptionLabel={(option) =>
            typeof option === 'string' ? option : option.title || ''
          }
          onChange={(_, value) => {
            if (value?._id) {
              navigate(`/movie/${value._id}`);
            }
          }}
          noOptionsText="No matching movies"
          sx={{ flex: 1 }}
          renderOption={(props, option) => (
            <Box
              component="li"
              {...props}
              sx={{
                display: 'flex',
                gap: 2,
                alignItems: 'flex-start'
              }}
            >
              <Avatar
                variant="rounded"
                src={option.poster || 'https://via.placeholder.com/60x90'}
                alt={option.title}
                sx={{ width: 56, height: 80 }}
              />

              <Box sx={{ overflow: 'hidden' }}>
                <Typography fontWeight={600} noWrap>
                  {option.title}
                </Typography>

                <Typography
                  variant="body2"
                  color="text.secondary"
                  sx={{
                    display: '-webkit-box',
                    WebkitLineClamp: 2,
                    WebkitBoxOrient: 'vertical',
                    overflow: 'hidden'
                  }}
                >
                  {option.description || 'No description'}
                </Typography>

                <Typography variant="caption">
                  <StarIcon fontSize="inherit" /> {option.rating ?? '‚Äî'}
                </Typography>
              </Box>
            </Box>
          )}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Search movies by name or description"
              size="medium"
              fullWidth
            />
          )}
        />

        <Button
          type="submit"
          variant="contained"
          size="large"
          disabled={loading}
          sx={{ px: 4 }}
        >
          Search
        </Button>
      </Box>

      {/* Loading */}
      {loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <CircularProgress />
        </Box>
      )}

      {/* Error */}
      {error && (
        <Typography color="error" align="center" sx={{ mt: 3 }}>
          {error}
        </Typography>
      )}

      {/* No results (only after search) */}
      {!loading && searched && movies.length === 0 && (
        <Typography align="center" sx={{ mt: 4 }}>
          No movies found.
        </Typography>
      )}

      {/* Search results */}
      <Grid container spacing={2}>
        {movies.map((movie) => (
          <Grid item key={movie._id} xs={12} sm={6} md={4} lg={3}>
            <MovieCard movie={movie} />
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
