import React, { useState } from 'react';
import API from '../api/api';
import { Box, Typography, TextField, Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';

export default function AdminAdd(){
  const [form, setForm] = useState({ title:'', description:'', rating:'', duration:'', releaseDate:'', poster:'' });
  const nav = useNavigate();

  async function submit(e) {
    e.preventDefault();
    try {
      const payload = {
        ...form,
        rating: form.rating ? Number(form.rating) : undefined,
        duration: form.duration ? Number(form.duration) : undefined
      };
      await API.post('/movies', payload);
      nav('/');
    } catch (err) {
      alert(err.response?.data?.message || 'Add failed');
    }
  }

  return (
    <Box maxWidth={720} mx="auto" sx={{ py: 4 }}>
      <Typography variant="h5" mb={2}>Add Movie</Typography>
      <Box component="form" onSubmit={submit} sx={{ display:'flex', flexDirection:'column', gap:2 }}>
        <TextField label="Title" value={form.title} onChange={(e)=>setForm({...form, title:e.target.value})} required />
        <TextField label="Description" value={form.description} onChange={(e)=>setForm({...form, description:e.target.value})} multiline rows={4} />
        <TextField label="Rating" value={form.rating} onChange={(e)=>setForm({...form, rating:e.target.value})} />
        <TextField label="Duration (min)" value={form.duration} onChange={(e)=>setForm({...form, duration:e.target.value})} />
        <TextField label="Release Date (YYYY-MM-DD)" value={form.releaseDate} onChange={(e)=>setForm({...form, releaseDate:e.target.value})} />
        <TextField label="Poster URL" value={form.poster} onChange={(e)=>setForm({...form, poster:e.target.value})} />
        <Box sx={{ display:'flex', gap:2 }}>
          <Button type="submit" variant="contained">Add</Button>
          <Button variant="outlined" onClick={()=>nav('/')}>Cancel</Button>
        </Box>
      </Box>
    </Box>
  );
}
