import React, { useEffect, useState } from 'react';
import API from '../api/api';
import {
  Grid,
  Box,
  Typography,
  Select,
  MenuItem,
  FormControl,
  Pagination,
  ToggleButton,
  ToggleButtonGroup,
  CircularProgress
} from '@mui/material';
import MovieCard from '../components/MovieCard';

export default function Home() {
  const [allMovies, setAllMovies] = useState([]); // full sorted list
  const [movies, setMovies] = useState([]);       // paginated slice
  const [page, setPage] = useState(1);
  const [limit] = useState(12);
  const [totalPages, setTotalPages] = useState(1);
  const [sortBy, setSortBy] = useState('title');
  const [view, setView] = useState('grid');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Fetch sorted movies once (on sort change)
  useEffect(() => {
    fetchSortedMovies();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [sortBy]);

  async function fetchSortedMovies() {
    try {
      setLoading(true);
      setError('');

      const res = await API.get('/movies/sorted', {
        params: { by: sortBy, order: 'asc' }
      });

      const payload = res.data;

      const list = Array.isArray(payload)
        ? payload
        : Array.isArray(payload.movies)
        ? payload.movies
        : [];

      setAllMovies(list);
      setTotalPages(Math.ceil(list.length / limit));
      setPage(1);
      setMovies(list.slice(0, limit));
    } catch (err) {
      console.error(err);
      setError('Failed to load movies');
      setAllMovies([]);
      setMovies([]);
    } finally {
      setLoading(false);
    }
  }

  function handlePage(_, value) {
    const start = (value - 1) * limit;
    const end = start + limit;
    setMovies(allMovies.slice(start, end));
    setPage(value);
  }

  return (
    <Box>
      {/* HEADER */}
      <Box
        sx={{
          display: 'flex',
          flexDirection: { xs: 'column', sm: 'row' },
          justifyContent: 'space-between',
          alignItems: { xs: 'flex-start', sm: 'center' },
          mb: 2,
          gap: 2
        }}
      >
        <Typography variant="h5" fontWeight={600}>
          All Movies
        </Typography>

        <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
          <FormControl size="small" sx={{ minWidth: 160 }}>
            <Select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
            >
              <MenuItem value="title">Name</MenuItem>
              <MenuItem value="rating">Rating</MenuItem>
              <MenuItem value="releaseDate">Release Date</MenuItem>
              <MenuItem value="duration">Duration</MenuItem>
            </Select>
          </FormControl>

          <ToggleButtonGroup
            value={view}
            exclusive
            size="small"
            onChange={(_, v) => v && setView(v)}
          >
            <ToggleButton value="grid">Grid</ToggleButton>
            <ToggleButton value="list">List</ToggleButton>
          </ToggleButtonGroup>
        </Box>
      </Box>

      {/* LOADING */}
      {loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <CircularProgress />
        </Box>
      )}

      {/* ERROR */}
      {error && (
        <Typography color="error" align="center" sx={{ mt: 3 }}>
          {error}
        </Typography>
      )}

      {/* EMPTY */}
      {!loading && !error && movies.length === 0 && (
        <Typography align="center" sx={{ mt: 4 }}>
          No movies available.
        </Typography>
      )}

      {/* MOVIES */}
      <Grid container spacing={2}>
        {movies.map((movie) => (
          <Grid
            item
            key={movie._id}
            xs={12}
            sm={view === 'grid' ? 6 : 12}
            md={view === 'grid' ? 4 : 12}
            lg={view === 'grid' ? 3 : 12}
          >
            <MovieCard movie={movie} view={view} />
          </Grid>
        ))}
      </Grid>

      {/* PAGINATION */}
      {totalPages > 1 && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <Pagination
            count={totalPages}
            page={page}
            onChange={handlePage}
            color="primary"
          />
        </Box>
      )}
    </Box>
  );
}
