import React, { useState } from 'react';
import API from '../api/api';
import { Box, TextField, Button, Grid, Typography } from '@mui/material';
import MovieCard from '../components/MovieCard';

export default function SearchPage() {
  const [q, setQ] = useState('');
  const [movies, setMovies] = useState([]);

  async function handleSearch(e) {
    e.preventDefault();
    if (!q.trim()) return;
    const res = await API.get('/movies/search', { params: { q }});
    setMovies(res.data);
  }

  return (
    <Box>
      <Typography variant="h5" mb={2}>Search Movies</Typography>
      <Box component="form" onSubmit={handleSearch} sx={{ display: 'flex', gap: 1, mb: 3 }}>
        <TextField value={q} onChange={(e)=>setQ(e.target.value)} label="Search by title or description" fullWidth size="small"/>
        <Button type="submit" variant="contained">Search</Button>
      </Box>

      <Grid container spacing={2}>
        {movies.map(m => (
          <Grid item key={m._id} xs={12} sm={6} md={4} lg={3}>
            <MovieCard movie={m} />
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
