import React, { useEffect, useState } from 'react';
import API from '../api/api';
import { Box, Typography, TextField, Button } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';

export default function AdminEdit(){
  const { id } = useParams();
  const nav = useNavigate();
  const [form, setForm] = useState(null);

  useEffect(() => {
    API.get('/movies', { params: { page: 1, limit: 1000 }}).then(res => {
      const found = res.data.movies.find(m => m._id === id);
      setForm(found || {});
    }).catch(console.error);
  }, [id]);

  async function submit(e) {
    e.preventDefault();
    try {
      await API.put(`/movies/${id}`, form);
      nav('/');
    } catch (err) {
      alert(err.response?.data?.message || 'Update failed');
    }
  }

  async function handleDelete() {
    if (!confirm('Delete movie?')) return;
    await API.delete(`/movies/${id}`);
    nav('/');
  }

  if (!form) return <Box>Loading...</Box>;

  return (
    <Box maxWidth={720} mx="auto" sx={{ py: 4 }}>
      <Typography variant="h5" mb={2}>Edit Movie</Typography>
      <Box component="form" onSubmit={submit} sx={{ display:'flex', flexDirection:'column', gap:2 }}>
        <TextField label="Title" value={form.title || ''} onChange={(e)=>setForm({...form, title:e.target.value})} required />
        <TextField label="Description" value={form.description || ''} onChange={(e)=>setForm({...form, description:e.target.value})} multiline rows={4} />
        <TextField label="Rating" value={form.rating || ''} onChange={(e)=>setForm({...form, rating:e.target.value})} />
        <TextField label="Duration" value={form.duration || ''} onChange={(e)=>setForm({...form, duration:e.target.value})} />
        <TextField label="Release Date" value={form.releaseDate ? form.releaseDate.split('T')[0] : ''} onChange={(e)=>setForm({...form, releaseDate:e.target.value})} />
        <TextField label="Poster URL" value={form.poster || ''} onChange={(e)=>setForm({...form, poster:e.target.value})} />
        <Box sx={{ display:'flex', gap:2 }}>
          <Button type="submit" variant="contained">Save</Button>
          <Button variant="outlined" color="error" onClick={handleDelete}>Delete</Button>
        </Box>
      </Box>
    </Box>
  );
}
