import React, { createContext, useEffect, useState } from 'react';
import API, { setToken } from '../api/api';

export const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem('movieapp_user') || 'null');
    } catch { return null; }
  });

  useEffect(() => {
    if (user?.token) {
      setToken(user.token);
      localStorage.setItem('movieapp_user', JSON.stringify(user));
    } else {
      setToken(null);
      localStorage.removeItem('movieapp_user');
    }
  }, [user]);

  async function login(email, password) {
    const res = await API.post('/auth/login', { email, password });
    const payload = { token: res.data.token, ...res.data.user };
    setUser(payload);
    return payload;
  }

  function logout() {
    setUser(null);
  }

  return <AuthContext.Provider value={{ user, login, logout, setUser }}>{children}</AuthContext.Provider>;
}
