import React, { useContext } from 'react';
import {
  Card,
  CardMedia,
  CardContent,
  Typography,
  CardActions,
  Button,
  Box
} from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';
import StarIcon from '@mui/icons-material/Star';
import { AuthContext } from '../contexts/AuthContext';
import API from '../api/api';

export default function MovieCard({ movie }) {
  const { admin } = useContext(AuthContext);
  const navigate = useNavigate();

  async function handleDelete() {
    if (!window.confirm('Delete this movie?')) return;
    try {
      await API.delete(`/movies/${movie._id}`);
      window.location.reload(); // simple & reliable refresh
    } catch (err) {
      alert(err.response?.data?.message || 'Delete failed');
    }
  }

  return (
    <Card
      sx={{
        display: 'flex',
        flexDirection: { xs: 'column', sm: 'row' },
        height: '100%'
      }}
    >
      <CardMedia
        component="img"
        src={movie.poster || 'https://via.placeholder.com/300x450?text=No+Image'}
        alt={movie.title}
        sx={{
          width: { xs: '100%', sm: 160 },
          height: { xs: 240, sm: 'auto' },
          objectFit: 'cover'
        }}
      />

      <CardContent sx={{ flex: 1 }}>
        <Typography variant="h6" noWrap>
          {movie.title}
        </Typography>

        <Typography
          variant="body2"
          sx={{
            mt: 1,
            display: '-webkit-box',
            WebkitLineClamp: 3,
            WebkitBoxOrient: 'vertical',
            overflow: 'hidden'
          }}
        >
          {movie.description || 'No description available.'}
        </Typography>

        <Typography variant="caption" display="block" sx={{ mt: 1 }}>
          <StarIcon fontSize="small" sx={{ verticalAlign: 'middle' }} />{' '}
          {movie.rating ?? '—'}
        </Typography>
      </CardContent>

      <CardActions
        sx={{
          alignItems: 'center',
          justifyContent: 'space-between',
          px: 2,
          pb: { xs: 2, sm: 0 },
          flexDirection: { xs: 'row', sm: 'column' }
        }}
      >
        {/* View button (always visible) */}
        <Button
          component={Link}
          to={`/movie/${movie._id}`}
          size="small"
        >
          View
        </Button>

        {/* ADMIN CONTROLS */}
        {admin && (
          <Box sx={{ display: 'flex', gap: 1, mt: { sm: 1 } }}>
            <Button
              size="small"
              onClick={() => navigate(`/admin/edit/${movie._id}`)}
            >
              Edit
            </Button>
            <Button
              size="small"
              color="error"
              onClick={handleDelete}
            >
              Delete
            </Button>
          </Box>
        )}
      </CardActions>
    </Card>
  );
}
